import React from "react";
import Image from "./chip.JPG";

const CardNumber= (number)=> {
  let regexNumber = /^[0-9]{16,}$/
  number=number.toString()
  let num=''
  for (let i=0;i<number.length;i+=4)
  {
    num=num+number.slice(i,i+4)+' '
  }
  return num
  }
  const Majuscule=(chaine)=>{
   chaine.toUppercase ();
  }


const Card = (props) => {
  return (
    <div className="card">
        <h1 className="company-name"> {Uppercase(props.CompanyName)}</h1>
        <img src ={Image} className="chip-phone" />
        <h1 className="card-number"> {CardNumber(props.CardNumber)}</h1>
        <div className="card-superposition">
          <div>
            <h1 className="valid-thru"> {props.ValidThru}</h1>
            <h1 className="card-holder"> {Uppercase(props.CardHolder)}</h1>
          </div>
          <div>
          <img className="mastercard" src='https://seeklogo.net/wp-content/uploads/2016/07/mastercard-vector-logo.png' />
          </div>
        </div>
      </div>
  );
};

export default Card;
