const Cardtwo = (props) => {
  return (<div className="component">
    <div className="card">
        <h1 className="company-name">Company name</h1>
        <img src ={Image} className="chip-phone" />
        <h1 className="card-number"> {CardNumber(props.CardNumber)}</h1>
        <div className="card-superposition">
          <div>
            <h1 className="valid-thru"> {ValidThru(props.ValidThru)}</h1>
            <h1 className="card-holder"> {Majuscule(props.CardHolder)}</h1>
          </div>
          <div>
          <img className="mastercard" src='https://seeklogo.net/wp-content/uploads/2016/07/mastercard-vector-logo.png' />
          </div>
        </div>
      </div>
      
      <div className="input">
          <div className="data"> 
      <input onChange={this.handleChange}name="code" placeholder="Enter Card Number" type='text' />
      </div> 
      <div className="data"> 
      <input placeholder="Enter Valid Thru" type='text' />
      </div> 
      <div className="data"> 
      <input placeholder="Enter Card Holder" type='text' />
      </div> 
      </div>
      </div>
  );
};

export default Cardtwo;
