import Image from "./chip.JPG";
import React, { Component } from "react";

class Card extends Component{
constructor(props) {
  super(props);

  this.state = {
  CardNumber: '1234567891234568',
  ValidThru : '12/80',
  CardHolder:'foulen',
  };}
  handleChange=(e)=>{
    this.setState({[e.target.CardNumber]:e.target.value.replace(/(.{4})/g, '$1 ').trim()})
    this.setState({[e.target.ValidThru]:e.target.value})
    this.setState({[e.target.CardHolder]:e.target.value})
  }
/*
const CardNumber = number => {
  //let regexNumber = /^[0-9]{16,}$/
  number = number.toString();
  let num = "";
  for (let i = 0; i < number.length; i += 4) {
    num = num + number.slice(i, i + 4) + " ";
  }
  return num;
};

const ValidThru = number => {
  number = number.toString();

  return number.slice(0, 2) + "/" + number.slice(2, 4);
};
Majuscule = chaine => {
  return (chaine = chaine.toUpperCase());
};
*/
 
render() {
  return (
    <div className="component">
      <div className="card">
        <h1 className="company-name">Company name</h1>
        <img src={Image} className="chip-phone" />
        <h1 className="card-number"> {(this.state.CardNumber)}</h1>
        <div className="card-superposition">
          <div>
            <h1 className="valid-thru"> {(this.state.ValidThru)}</h1>
            <h1 className="card-holder"> {(this.state.CardHolder)}</h1>
          </div>
          <div>
            <img
              className="mastercard"
              src="https://seeklogo.net/wp-content/uploads/2016/07/mastercard-vector-logo.png"
            />
          </div>
        </div>
      </div>
      <div className="input">
          <div className="data"> 
      <input className="output" name="code" placeholder="Enter Card Number" type='text' />
      </div> 
      <div className="data"> 
      <input className="output" onChange={this.handleChange} placeholder="Enter Valid Thru" type='text' />
      </div> 
      <div className="data"> 
      <input className="output" placeholder="Enter Card Holder" type='text' />
      </div> 
      </div>
    </div>
  );
};
}
export default Card;
